mapper.select:
SELECT MNO, MNAME, EMAIL
FROM MEMBERS
WHERE EMAIL = #{email} AND PWD = #{pwd}

SELECT MNO, MNAME, EMAIL, CRE_DATE
FROM MEMBERS
WHERE MNO = #{no}

mapper.update:
UPDATE MEMBERS
MNAME = #{name}, EMAIL = #{email}, PWD = #{password}, MOD_DATE = SYSDATE
WHERE MNO = #{no}

mapper.select:
SELECT IDX, STORED_FILE_NAME
FROM TB_FILE
WHERE PARENT_SEQ = #{no}
